      <div  >
        
          <Card style ={cardStyle} >
        <Card.Img variant='top' src="/movers.jpg " alt = "movers" style={{width:'50px', heiht:'50px', alignItem: 'center'}}  />

        <Card.Body >
          <Card.Title> World wide movers </Card.Title>

          <Card.Text>
            We are the best movers available at and for your service. 
          </Card.Text>

          <Row style={{
        backgroundColor: 'red',
      }}>
        Sample First Row
      </Row>
      <Row style={{
        backgroundColor: 'yellow',
      }}>
        Sample Second Row
      </Row>
      <Row style={{
        backgroundColor: 'green',
      }}>
        Sample Third Row
      </Row>

        </Card.Body>


          </Card>

        </div>
        
      
    )
  }
}
